histories:
{
  "encryption_methods": [
    {
      "name": "对称加密",
      "advantages": "1. 加密和解密速度快；2. 实现简单；3. 适用于大数据量的加密。",
      "disadvantages": "1. 密钥的安全性难以保证；2. 密钥的分发和管理困难；3. 不适用于跨网络的加密通信。"
    },
    {
      "name": "非对称加密",
      "advantages": "1. 密钥的安全性高；2. 可以实现加密通信的身份验证；3. 适用于跨网络的加密通信。",
      "disadvantages": "1. 加密和解密速度较慢；2. 实现复杂；3. 不适用于大数据量的加密。"
    },
    {
      "name": "哈希函数",
      "advantages": "1. 不可逆性，无法从哈希值反推原始数据；2. 计算速度快；3. 适用于验证数据完整性。",
      "disadvantages": "1. 不同的输入可能产生相同的哈希值（碰撞）；2. 无法恢复原始数据；3. 不适用于加密通信。"
    }
  ]
}

